import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL}/api/movies/latest`)
      .then(res => {
        console.log("FILME PRIMITE:", res.data);
        setMovies(res.data);
      })
      .catch(err => {
        console.error("EROARE API:", err);
        alert("Eroare la conectare cu backend-ul!");
      });
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>ðŸŽ¬ Lista de filme</h1>
      {movies.length === 0 && <p>Nu s-au gÄƒsit filme.</p>}
      {movies.map(movie => (
        <div key={movie.id} style={{ marginBottom: '1.5rem' }}>
          <h2>{movie.title}</h2>
          <p><b>Gen:</b> {movie.genre}</p>
          <p>{movie.description}</p>
          <img src={movie.imageUrl} alt={movie.title} width="300" />
        </div>
      ))}
    </div>
  );
}

export default App;
